"use strict";

(function initUser() {
  var email = localStorage.getItem('userEmail');
  $('.user-email').text(email);
})();

var monthNames = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
$('#start-chat').click(function startChat() {
  alert("Let's start!");
});

function newMessageInChat(userEmail, message, strDate) {
  $('<article/>', {
    class: 'chat-section-card',
    html: "<div>\n                <img src=\"./assets/speak.png\" class=\"communities-image\" />\n              </div>\n              <div class=\"chat-section-card-text\">\n                <span class=\"communities-name user-email\">\n                ".concat(userEmail, "\n                </span>\n                <div class=\"chat-section-message\">\n                  <p class=\"user-message\">\n                  ").concat(message, "\n                  </p>\n                </div>\n                  <p class=\"message-date\">\n                  ").concat(strDate, "\n                  </p>\n              </div>")
  }).appendTo('.chat-section-field');
  $('.chat-section-field').animate({
    scrollTop: $('.chat-section-field').last().prop('scrollHeight')
  });
}

$.getJSON('chat-message.json').done(function json(data) {
  $.each(data, function newChatElement(key, value) {
    var speakerName = value.speakerName,
        speakerMessage = value.speakerMessage,
        messageDate = value.messageDate;
    newMessageInChat(speakerName, speakerMessage, messageDate);
  });
});
$('#chat-message-text').keyup(function keyup(event) {
  var email = localStorage.getItem('userEmail');
  var message = $('textarea').val();
  var date = new Date();
  var strDate = date.getDate() + ' ' + monthNames[date.getMonth() + 1] + ' ' + date.getFullYear();

  if (event.keyCode === 13) {
    if (message === '') {
      alert('Enter Some Text In Textarea');
    } else {
      newMessageInChat(email, message, strDate);
    }

    $('textarea').val('');
  }
});
"use strict";

$('#login-form').submit(function submit(event) {
  var email = $('#email').val();
  var myComputer = $('#keep-sign').prop('checked');
  localStorage.setItem('userEmail', email);
  localStorage.setItem('myComputer', myComputer);
  window.location = 'chat.html';
  event.preventDefault();
});

(function onInit() {
  var userEmail = localStorage.getItem('userEmail');
  var myComputer = localStorage.getItem('myComputer');

  if (myComputer === 'true') {
    $('#email').val(userEmail);
    $('#keep-sign').prop('checked', !!myComputer);
  }
})();
